import React, { Component } from 'react'

export default class NewsItem extends Component {

    render() {
        let { title, description, url } = this.props;
        let mystls = { cursor: 'pointer' }
        return (
            <div>
                <div className="col">
                    <div className="card">
                        <img src="http://www.nftitalia.com/wp-content/uploads/2017/07/news-1-1600x429.jpg" className="card-img-top" alt="..." />
                        <a href={url} style={{textDecoration: 'none',color: 'black'}} target="_blank">
                            <div class="card-body" style={mystls}>
                                <h5 class="card-title">{title}...</h5>
                                <p class="card-text">{description}...</p>
                            </div>
                        </a>
                    </div>
                </div>
            </div>
        )
    }
}
