import React, { Component } from 'react'
import NewsItem from './NewsItem'

export default class News extends Component {
    constructor() {
        super();
        console.log(this.article);
        this.state = {
            article: this.article
        }
    }
    async componentDidMount() {
        let url="https://newsapi.org/v2/top-headlines?country=in&apiKey=ff149229124d4bf6b7a5965f6ad30cef";
        let Dat = await fetch(url);
        let data=await Dat.json();
        let article = data.article;
        console.log(article);
    }
    
    
    render() {
        return (
            <div  className="row row-cols-md-4 g-3 m-3">
                {this.state.article.map((element) => {
                    return <div>
                        <NewsItem key={element.url} title={element.title.slice(0,30)} description={element.description.slice(0,140)} author={element.author} url={element.url} />
                    </div>
                })}
            </div>
        )

    }
}
