import React, { Component } from 'react'
import NewsItem from './NewsItem'

export default class News extends Component {
    article = [];
    constructor() {
        super();
        this.state = {
            article: this.article,
        }
    }
    async componentDidMount() {
        let Url = `https://newsapi.org/v2/top-headlines?country=in&apiKey=ff149229124d4bf6b7a5965f6ad30cef&page=2`;
        let data = await fetch(Url);
        let parse = await data.json();
        this.setState({ article: parse.articles });
    }
    render() {
        return (
            <div className="row row-cols-md-4 g-3 m-3">
                {
                    this.state.article.map((ele) => {
                        return <div key={ele.url}>
                            <NewsItem title={ele.title === null ? '' : ele.title.slice(0, 45)} description={ele.description == null ? '' : ele.description} xurl={ele.url} imageUrl={ele.urlToImage === null ? "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS6wHc3OzKebPw9iQ9NMcjKRHSxIFKN2Ds2LQ&usqp=CAU" : ele.urlToImage} />
                        </div>
                    })
                }
                <div className="comtainer">
                    
                </div>
            </div>
        )

    }
}
