import React, { Component } from 'react'
import NewsItem from './NewsItem'

export default class News extends Component {
    article = [];
    constructor() {
        super();
        this.state = {
            article: this.article,
            // loading: false
        }
        // console.log(this.article);
    }
    async componentDidMount() {
        let Url = "https://newsapi.org/v2/top-headlines?country=in&apiKey=ff149229124d4bf6b7a5965f6ad30cef";
        let data = await fetch(Url);
        let parse= await data.json(); 
        console.log(parse);
        this.setState({ article: parse.article});
    }
    render() {
        return (
            <div className="row row-cols-md-4 g-3 m-3">
                {this.state.article.map((element)=> {
                    return <div key={element.url}>
                        <NewsItem title={element.title} description={element.description} xurl={element.url}/>
                    </div>
                })}
            </div>
        )
    }
}
