import React, { Component } from 'react'

export default class NewsItem extends Component {
    async componentDidMount() {
        let url="https://newsapi.org/v2/top-headlines?country=in&apiKey=ff149229124d4bf6b7a5965f6ad30cef";
        let Dat = await fetch(url);
        let data=await Dat.json();
        article = data.article;
        console.log(article);
    }
    render() {
        let { title, description, author, url } = this.props;
        let mystls = { cursor: 'pointer' }
        return (
            <div>
                <div className="col">
                    <div className="card">
                        <img src="http://www.nftitalia.com/wp-content/uploads/2017/07/news-1-1600x429.jpg" className="card-img-top" alt="..." />
                        <a href={url} style={{textDecoration: 'none',color: 'black'}} target="_blank">
                            <div class="card-body" style={mystls}>
                                <h5 class="card-title">{title}...</h5>
                                <p class="card-text">{description}...</p>
                            </div>
                        </a>
                    </div>
                </div>
            </div>
        )
    }
}
